import React from 'react';
import "./ListItem.scss";
import { useState, useEffect } from 'react';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import AddIcon from '@mui/icons-material/Add';
import ThumbUpAltOutlinedIcon from '@mui/icons-material/ThumbUpAltOutlined';
import ThumbDownAltOutlinedIcon from '@mui/icons-material/ThumbDownAltOutlined';
import axios from 'axios';

function ListItem({index,item}) {
//item is just a id, we should use the id to get the data

  const [isHovered,setIsHovered] =useState(false);
  const [movie,setMovie] =useState({});
  useEffect(() => { 
    const getMovie = async () => {
      try{
        const res = await axios.get("movies/find/"+item, {
          headers: {
            token:"Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzZmJjNmFkNWIyNzJiYmViNWVlZGNiZiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY3NzQ1Njk4OSwiZXhwIjoxNjgwMDQ4OTg5fQ.wAnmjhC0za1c3F-cGgbB7PLJSPyl3QiPiR5_HxnFaRY"
          }
        });
        setMovie(res.data)
      }catch(err) {
        console.log(err);
      }

      
    };
    getMovie();

  }, [item])

  return (

    <Link to="/watch">
      
    </Link>
    
  )
}

export default ListItem
