import React from 'react';
import "./ListItem.scss";
import { useState, useEffect } from 'react';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import AddIcon from '@mui/icons-material/Add';
import ThumbUpAltOutlinedIcon from '@mui/icons-material/ThumbUpAltOutlined';
import ThumbDownAltOutlinedIcon from '@mui/icons-material/ThumbDownAltOutlined';
import axios from 'axios';

function ListItem({index,item}) {
//item is just a id, we should use the id to get the data

  const [isHovered,setIsHovered] =useState(false);
  const [movie,setMovie] =useState({});
  useEffect(() => { 
    const getMovie = async () => {
      try{
        const res = await axios.get("movies/find/"+item, {
          headers: {
            token:"Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzZmJjNmFkNWIyNzJiYmViNWVlZGNiZiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY3NzQ1Njk4OSwiZXhwIjoxNjgwMDQ4OTg5fQ.wAnmjhC0za1c3F-cGgbB7PLJSPyl3QiPiR5_HxnFaRY"
          }
        });
      }catch(err) {
        console.log(err);
      }

      setMovie(res.data)
    };
    getMovie();

  }, [item])

  return (
    <div 
    className='ListItem'
    style={{left:isHovered&& index*225-50 + index*2.5}}
    onMouseEnter={()=>setIsHovered(true)}
    onMouseLeave={()=>setIsHovered(false)}>
      <img 
      src={movie.img}
      alt="" />
    {isHovered &&(
      <>
      <video src={movie.trailer} autoPlay={true} loop/>
      <div className="itemInfo">
            <div className="icons">
              <PlayArrowIcon className="icon" />
              <AddIcon className="icon" />
              <ThumbUpAltOutlinedIcon className="icon" />
              <ThumbDownAltOutlinedIcon className="icon" />
            </div>
            <div className="itemInfoTop">
              <span>{movie.duration}</span>
              <span className="limit">+{movie.limit}</span>
              <span>{movie.year}</span>
            </div>
            <div className="desc">
              {item.desc}
            </div>
            <div className="genre">{item.genre}</div>
          </div>
          </>
        )}
    </div>
  )
}

export default ListItem
